import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import {
  Container, Typography, Tabs, Tab, Paper, Table, TableHead, TableRow, TableCell, TableBody,
  IconButton, Button, ThemeProvider, CssBaseline, Dialog, DialogTitle, DialogContent, DialogActions
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import { styled } from '@mui/system';
import { useNavigate } from 'react-router-dom';
import theme from './Theme/theme';

const AppContainer = styled(Container)(({ theme }) => ({
  ...theme.root,
  zIndex: 9999,
}));

const AdminBoard = ({ onLogout }) => {
  const [tabIndex, setTabIndex] = useState(0);
  const [walkways, setWalkways] = useState([]);
  const [users, setUsers] = useState([]);
  const [confirmationDialog, setConfirmationDialog] = useState({ open: false, type: '', id: '' });

  const navigate = useNavigate();

  useEffect(() => {
    Axios.get('http://localhost:8080/allWalkways', { withCredentials: true })
      .then(({ data }) => setWalkways(data))
      .catch(err => console.error('Error loading walkways:', err));

    Axios.get('http://localhost:8080/allUsers', { withCredentials: true })
      .then(({ data }) => setUsers(data))
      .catch(err => console.error('Error loading users:', err));
  }, []);

  const handleDelete = async (type, id) => {
    try {
      if (type === 'user') {
        await Axios.delete(`http://localhost:8080/deleteUser/${id}`, { withCredentials: true });
        setUsers(prev => prev.filter(u => u.id !== id));
      } else if (type === 'walkway') {
        await Axios.delete(`http://localhost:8080/deleteWalkway/${id}`, { withCredentials: true });
        setWalkways(prev => prev.filter(w => w.id !== id));
      }
      setConfirmationDialog({ open: false, type: '', id: '' });
    } catch (error) {
      console.error(`Error deleting ${type}:`, error);
    }
  };

  const handleApprove = async (id) => {
    try {
      await Axios.post(`http://localhost:8080/approveCityCouncil/${id}`, {}, { withCredentials: true });
      setUsers(prev => prev.map(u => u.id === id ? { ...u, status: 'approved' } : u));
    } catch (err) {
      console.error('Error approving authority:', err);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppContainer>
        <Typography variant="h4" gutterBottom>Admin Dashboard</Typography>
        <Tabs value={tabIndex} onChange={(_, newValue) => setTabIndex(newValue)}>
          <Tab label="Utilizadores" />
          <Tab label="Passadiços" />
        </Tabs>

        {tabIndex === 0 && (
          <Paper sx={{ mt: 3, p: 2 }}>
            <Typography variant="h6">Lista de Utilizadores</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Nome</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Tipo</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {users.map((user) => (
                  <TableRow key={user.id}>
                    <TableCell>{user.name}</TableCell>
                    <TableCell>{user.email}</TableCell>
                    <TableCell>{user.type}</TableCell>
                    <TableCell>{user.status}</TableCell>
                    <TableCell>
                      {user.type === 'authority' && user.status === 'pending' && (
                        <IconButton onClick={() => handleApprove(user.id)} color="success">
                          <CheckCircleIcon />
                        </IconButton>
                      )}
                      <IconButton onClick={() => setConfirmationDialog({ open: true, type: 'user', id: user.id })} color="error">
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        )}

        {tabIndex === 1 && (
          <Paper sx={{ mt: 3, p: 2 }}>
            <Typography variant="h6">Lista de Passadiços</Typography>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Nome</TableCell>
                  <TableCell>Distrito</TableCell>
                  <TableCell>Região</TableCell>
                  <TableCell>Ações</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {walkways.map((w) => (
                  <TableRow key={w.id}>
                    <TableCell>{w.name}</TableCell>
                    <TableCell>{w.district}</TableCell>
                    <TableCell>{w.region}</TableCell>
                    <TableCell>
                      <IconButton onClick={() => setConfirmationDialog({ open: true, type: 'walkway', id: w.id })} color="error">
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Paper>
        )}

        <Dialog open={confirmationDialog.open} onClose={() => setConfirmationDialog({ open: false, type: '', id: '' })}>
          <DialogTitle>Confirmação</DialogTitle>
          <DialogContent>
            Tem a certeza que deseja eliminar este {confirmationDialog.type === 'user' ? 'utilizador' : 'passadiço'}?
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setConfirmationDialog({ open: false, type: '', id: '' })}>Cancelar</Button>
            <Button onClick={() => handleDelete(confirmationDialog.type, confirmationDialog.id)} color="error">Eliminar</Button>
          </DialogActions>
        </Dialog>

      </AppContainer>
    </ThemeProvider>
  );
};

export default AdminBoard;
