const handleStartWalkClick = async () => {
  // navigate to the start walk page
  navigate('/StartWalk');
};

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogoClick = () => {
    navigate('/WalkerBoard');
  };

  const handleCloseDialog = () => {
    setSelectedMarker(null);
  };
  const handleMarkerClick = useCallback(async (marker) => {
    setSelectedMarker(marker);
}, []);


  useEffect(() => {
    if (selectedMarker) {
      // Calculate difficulty when the selected marker changes
      const calculateDifficulty = (difficultyLevel) => {
        if (difficultyLevel === 1) {
          return 'Easy';
        } else if (difficultyLevel === 2) {
          return 'Medium';
        } else if (difficultyLevel === 3) {
          return 'Hard';
        } else {
          return 'Unknown';
        }
      };

      setDifficulty(calculateDifficulty(selectedMarker.difficulty));
    }
  }, [selectedMarker]);

  useEffect(() => {
    const fetchMarkers = async () => {
      try {
        const response = await Axios.get("http://localhost:8080/markers");
        setMarkers(response.data);
      } catch (error) {
        setError('Failed to fetch markers: ' + error.message);
      }
    };

    fetchMarkers();
  }, []);

  const transformedMarkers = markers
    .filter(marker => marker.coordinates) // Filter out any marker without coordinates
    .map(marker => ({
      ...marker,
      position: {
        lat: marker.coordinates.latitude,
        lng: marker.coordinates.longitude,
      },
      name: marker.name || 'Unknown Name', // Fallback for missing names
      description: marker.description || 'No description available', // Fallback for missing descriptions
      image: marker.primaryImage || 'default_image.jpg', // Fallback for missing image
      distance: marker.specifics?.distance || 'Unknown Distance', // Safe access for nested properties
      difficulty: marker.specifics?.difficulty || 'Unknown Difficulty',
      region: marker.region || 'Unknown Region',
      district: marker.district || 'Unknown District',
      // get default center to map for each marker
      defaultCenter: {
        lat: marker.coordinates.latitude,
        lng: marker.coordinates.longitude,
      },
  }));

  // Fetch GeoJSON Data based on selected marker
  useEffect(() => {
    if (selectedMarker) {
        console.log("Fetching GeoJSON data for walkway:", selectedMarker.id);
        Axios.get(`http://localhost:8080/getGeojson`, { params: { walkwayId: selectedMarker.id } })
            .then((response) => {
                const geojsonData = response.data.geojson;
                console.log('GeoJSON data:', geojsonData);

                // Ensure that the features array exists and is valid
                if (geojsonData && Array.isArray(geojsonData.features) && geojsonData.features.length > 0) {
                    const firstFeature = geojsonData.features[0];
                    console.log('First feature:', firstFeature);

                    if (firstFeature.geometry && Array.isArray(firstFeature.geometry.coordinates)) {
                        const coordinates = firstFeature.geometry.coordinates;
                        console.log('Coordinates:', coordinates);

                        // Set the center of the map to the first feature's coordinates if it's a Point
                        if (firstFeature.geometry.type === 'Point') {
                            setCenter({ lat: coordinates[1], lng: coordinates[0] });
                        }
                    } else {
                        console.warn('No valid geometry coordinates found in the first feature.');
                    }
                } else {
                    console.warn('No valid GeoJSON features found.');
                }

                setGeojsonData(geojsonData);
            })
            .catch((error) => {
                console.error('Error fetching GeoJSON:', error);
            });
    }
}, [selectedMarker]);


  // Render GeoJSON Data on the map when it's available
  useEffect(() => {
    if (mapRef.current && geojsonData) {
      // Clear previous GeoJSON data
      mapRef.current.data.forEach((feature) => {
        mapRef.current.data.remove(feature);
      });

      // Add new GeoJSON data
      mapRef.current.data.addGeoJson(geojsonData);

      // Set style for LineString and Points
      mapRef.current.data.setStyle((feature) => {
        if (feature.getGeometry().getType() === 'LineString') {
          return {
            strokeColor: 'blue',
            strokeWeight: 3,
            clickable: false,
          };
        } else if (feature.getGeometry().getType() === 'Point') {
          return {
            icon: {
              path: window.google.maps.SymbolPath.CIRCLE,
              scale: 6,
              fillColor: 'red',
              fillOpacity: 1,
              strokeWeight: 2,
              strokeColor: 'white',
            },
          };
        }
      });
    }
  }, [geojsonData]);


  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppContainer>
        <Logo src={logo} alt="logo" onClick={handleLogoClick} />

        <MoreMenuButton
          aria-label="more"
          aria-controls="long-menu"
          aria-haspopup="true"
          onClick={handleClick}
        >
        <MoreVertIcon />
        </MoreMenuButton>

        <Menu
          id="long-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
          slotProps={{
            paper: {
              style: {
                maxHeight: 48 * 4.5,
                width: '20ch',
              },
            },
          }}
        >
          <MenuItem onClick={() => { handleClose(); navigate('/Profile'); }}>
            Profile
          </MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/History'); }}>
            History
          </MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/Favorites'); }}>
            Favorites
          </MenuItem>
        </Menu>

        <MapContainer>
          <APIProvider 
            apiKey={'AIzaSyC8Sj12_rv73anPhi2oCeqsVVjUkfFJ4-U'} onLoad={() => console.log('Maps API has loaded.')}>
            <Map
              defaultZoom={10}
              defaultCenter={{ lat: 41.5564, lng: -8.16415 }}
              mapId='5f6b01e0c09b0450'
              mapTypeId="terrain" 
            >
              {transformedMarkers.map((marker) => (
                <AdvancedMarker
                  key={marker.id}
                  position={marker.position}
                  clickable={true}
                  onClick={() => handleMarkerClick(marker)}
                >
                <Pin background={theme.palette.primary.secondary} borderColor={theme.palette.primary.main} glyphColor={theme.palette.primary.contrastText}
                   />
                </AdvancedMarker>
              ))}
            </Map>
          </APIProvider>
        </MapContainer>

        <Dialog open={selectedMarker !== null} onClose={handleCloseDialog} fullWidth>
          <DialogTitle>
            {selectedMarker?.name}
            <IconButton aria-label="close" onClick={handleCloseDialog}>
              <CloseIcon />
            </IconButton>
          </DialogTitle>

          <Tabs value={tabIndex} onChange={handleTabChange}>
            <Tab label="Overview" />
            <Tab label="Walkway" />
            <Tab label="Services" />
          </Tabs>

          <DialogContent dividers>
            {tabIndex === 0 && (
              <>
                <img src={imageMap[selectedMarker?.id]} alt={selectedMarker?.name} style={{ maxWidth: '100%', marginBottom: '20px' }} />
                <Typography gutterBottom color="primary"><strong>District:</strong> {selectedMarker?.district}</Typography>
                <Typography gutterBottom color="primary"><strong>Region:</strong> {selectedMarker?.region}</Typography>
                <Typography gutterBottom color="secondary"><strong>Difficulty:</strong> {difficulty}</Typography>
                <Typography gutterBottom><strong>Description:</strong> {selectedMarker?.description}</Typography>
              </>
            )}
            {tabIndex === 1 && selectedMarker && (
              <div>
                <APIProvider apiKey={'AIzaSyC8Sj12_rv73anPhi2oCeqsVVjUkfFJ4-U'} onLoad={() => console.log('Maps API of Walkway has loaded.')}>
                  <Map
                    zoom={12}  
                    defaultCenter={selectedMarker?.defaultCenter}  
                    mapId='5f6b01e0c09b0450'x
                    onLoad={(map) => {
                      mapRef.current = map;
                    }}
                    style={{ height: '600px', width: '100%' }}
                  >
                  </Map>
                </APIProvider>
              </div>
            )}
            {tabIndex === 2 && (
              <Typography>List of services offered along the walkway can be added here.</Typography>
            )}
          </DialogContent>

          <DialogActions>
            <div>
              {!isFavorite ? (
                <Button variant="contained" color="primary" startIcon={<FavoriteIcon />} onClick={handleFavoriteClick}>
                  Mark as Favorite
                </Button>
              ) : (
                <Button variant="contained" disabled>
                  This location is already marked as favorite!
                </Button>
              )}

              {error && <div style={{ color: "red" }}>{error}</div>}
              {success && <div style={{ color: "green" }}>{success}</div>}
            </div>
            <Button variant="contained" color="secondary" startIcon={<DirectionsWalkIcon />} onClick={handleStartWalkClick}>
              Start Walk
            </Button>
          </DialogActions>
        </Dialog>
        <LogoutButton variant="contained" color="secondary" onClick={handleLogOut}>
          Logout
        </LogoutButton>
      </AppContainer>
    </ThemeProvider>
  );
};

export default WalkerBoard;
