                </DialogActions>              
              </form>
            </DialogContent>
          </Dialog>
          <LogoutButton variant="contained" color="secondary" onClick={handleLogOut}>Logout</LogoutButton>
        </APIProvider>
      </AppContainer>
    </ThemeProvider>
  );
};

