import React, { useEffect, useState } from "react";
import Axios from "axios";
import { Menu, MenuItem, Dialog, DialogContent, IconButton, Button, Container, DialogTitle, CssBaseline, Typography, Paper, ThemeProvider } from '@mui/material';
import { styled } from '@mui/system';
import MapMarkerIcon from '@mui/icons-material/Place';
import CloseIcon from '@mui/icons-material/Close';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import { useNavigate } from 'react-router-dom';

import theme from './Theme/theme';
import logo from './Theme/images/baselogo.jpg';
import walkway0 from './Theme/images/walkway_0.jpg';
import walkway1 from './Theme/images/walkway_1.jpg';
import walkway2 from './Theme/images/walkway_2.jpg';
import walkway3 from './Theme/images/walkway_3.jpg';

// Styled components using MUI's new styled API
const AppContainer = styled(Container)(({ theme }) => ({
  ...theme.root,
  zIndex: 9999,
}));

const Logo = styled('img')(({ theme }) => ({
  position: 'absolute',
  top: theme.spacing(2),
  left: theme.spacing(50),
  width: '200px',
  height: 'auto',
  cursor: 'pointer',
}));

const MapContainer = styled(Paper)(({ theme }) => ({
  height: '600px',
  width: '100%',
  marginTop: theme.spacing(4),
}));

const LogoutButton = styled(Button)(({ theme }) => ({
  position: 'absolute',
  right: 100,
  top: 20,
}));

const MoreMenuButton = styled(IconButton)(({ theme }) => ({
  position: 'absolute',
  right: 20,
  top: 20,
}));

const DialogTitleContainer = styled('div')({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
});

const CityCouncilBoard = ({ onLogout }) => {
  const [error, setError] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [success, setSuccess] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [userId, setUserId] = useState(null); // ID do utilizador logado
  const navigate = useNavigate();
  const imageMap = {
    0: walkway0,
    1: walkway1,
    2: walkway2,
    3: walkway3,
  };

  useEffect(() => {
    // Fetch o ID do utilizador logado
    const fetchUserId = async () => {
      try {
        const response = await Axios.get("http://localhost:8080/user");
        setUserId(response.data.userId);
      } catch (error) {
        console.error("Erro ao buscar o ID do utilizador:", error);
      }
    };

    fetchUserId();
  }, []);

  // Fetch apenas os "walkways" associados ao ID do utilizador logado
  useEffect(() => {
    const fetchMarkers = async () => {
      try {
        const response = await Axios.get(`http://localhost:8080/markers?userId=${userId}`);
        setMarkers(response.data);
      } catch (error) {
        console.error("Erro ao buscar os marcadores:", error);
        setError("Erro ao buscar os marcadores.");
      }
    };

    if (userId) {
      fetchMarkers();
    }
  }, [userId]);

  const handleAddWalkway = async (newWalkwayData) => {
    try {
      await Axios.post("http://localhost:8080/addWalkway", { ...newWalkwayData, userId });
      setSuccess("Walkway adicionada com sucesso.");
      // Atualiza a lista de walkways após adicionar um novo
      setMarkers([...markers, newWalkwayData]);
    } catch (error) {
      console.error("Erro ao adicionar walkway:", error);
      setError("Erro ao adicionar walkway.");
    }
  };

  const handleUpdateWalkway = async (updatedWalkwayData) => {
    try {
      await Axios.put(`http://localhost:8080/walkways/${updatedWalkwayData.id}`, updatedWalkwayData);
      setSuccess("Walkway atualizada com sucesso.");
      // Atualiza a lista de walkways após modificar
      setMarkers(markers.map(marker => marker.id === updatedWalkwayData.id ? updatedWalkwayData : marker));
    } catch (error) {
      console.error("Erro ao atualizar walkway:", error);
      setError("Erro ao atualizar walkway.");
    }
  };

  const handleDeleteWalkway = async (walkwayId) => {
    try {
      await Axios.delete(`http://localhost:8080/walkways/${walkwayId}`);
      setSuccess("Walkway removida com sucesso.");
      // Remove o marcador da lista
      setMarkers(markers.filter(marker => marker.id !== walkwayId));
    } catch (error) {
      console.error("Erro ao remover walkway:", error);
      setError("Erro ao remover walkway.");
    }
  };

  const handleLogOut = async () => {
    setError(null);
    setSuccess(null);

    try {
      await Axios.get("http://localhost:8080/logout");
      if (onLogout) {
        onLogout();
      }

      setSuccess('Log out successful!');
      navigate('/App');
    } catch (error) {
      setError('Log out failed: ' + error.message);
    }
  };

  const handleCloseDialog = () => {
    setSelectedMarker(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppContainer>
        <Logo src={logo} alt="logo" onClick={() => navigate('/WalkerBoard')} />
        {/* Menu de três pontos */}
        <MoreMenuButton onClick={(e) => setAnchorEl(e.currentTarget)}>
          <MoreVertIcon />
        </MoreMenuButton>
        <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={() => setAnchorEl(null)}>
          <MenuItem onClick={() => navigate('/Profile')}>Perfil</MenuItem>
          <MenuItem onClick={() => navigate('/History')}>Histórico</MenuItem>
          <MenuItem onClick={() => navigate('/Settings')}>Configurações</MenuItem>
        </Menu>

        {/* Mapa Google */}
        <MapContainer>
          <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <GoogleMap
              mapContainerStyle={{ height: '100%', width: '100%' }}
              zoom={10}
              center={{ lat: 41.5564, lng: -8.16415 }}
              mapTypeId="terrain"
            >
              {markers.map((marker) => (
                <Marker
                  key={marker.id}
                  position={{ lat: marker.coordinates.latitude, lng: marker.coordinates.longitude }}
                  title={marker.name}
                  onClick={() => setSelectedMarker(marker)}
                />
              ))}
            </GoogleMap>
          </LoadScript>
        </MapContainer>

        {/* Diálogo de Detalhes do Marcador */}
        <Dialog open={selectedMarker !== null} onClose={handleCloseDialog} fullWidth>
          <DialogTitleContainer>
            <Typography>{selectedMarker?.name}</Typography>
            <IconButton onClick={handleCloseDialog}><CloseIcon /></IconButton>
          </DialogTitleContainer>
          <DialogContent dividers>
            <img src={imageMap[selectedMarker?.id]} alt={selectedMarker?.name} style={{ maxWidth: '100%', marginBottom: '20px' }} />
            <Typography>Distrito: {selectedMarker?.district}</Typography>
            <Typography>Região: {selectedMarker?.region}</Typography>
            <Typography>Descrição: {selectedMarker?.description}</Typography>
            <Button onClick={() => handleUpdateWalkway(selectedMarker)}>Editar</Button>
            <Button onClick={() => handleDeleteWalkway(selectedMarker?.id)} color="secondary">Remover</Button>
          </DialogContent>
        </Dialog>

        {/* Botão de Logout */}
        <LogoutButton onClick={handleLogOut} variant="contained" color="secondary">
          Logout
        </LogoutButton>
      </AppContainer>
    </ThemeProvider>
  );
};

export default CityCouncilBoard;
