import React, { useEffect, useState, useRef, useCallback } from "react";
import Axios from "axios";
import { Menu, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, Tabs, Tab, IconButton, Button, Container, CssBaseline, Typography, Paper, ThemeProvider } from '@mui/material';
import { styled } from '@mui/system';
import CloseIcon from '@mui/icons-material/Close';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FavoriteIcon from '@mui/icons-material/Favorite';
import DirectionsWalkIcon from '@mui/icons-material/DirectionsWalk';
import { APIProvider, AdvancedMarker, Map, Pin } from '@vis.gl/react-google-maps'; 
import { useNavigate } from 'react-router-dom';

import theme from './Theme/theme';
import logo from './Theme/images/baselogo.jpg';
import walkway0 from './Theme/images/walkway_0.jpg';
import walkway1 from './Theme/images/walkway_1.jpg';
import walkway2 from './Theme/images/walkway_2.jpg';
import walkway3 from './Theme/images/walkway_3.jpg';

// Styled components using MUI's new styled API
const AppContainer = styled(Container)(({ theme }) => ({
  ...theme.root,
  zIndex: 9999,
}));

const Logo = styled('img')(({ theme }) => ({
  position: 'absolute',
  top: theme.spacing(2),
  left: theme.spacing(50),
  width: '200px',
  height: 'auto',
  cursor: 'pointer',
}));

const MapContainer = styled(Paper)(({ theme }) => ({
  height: '600px',
  width: '100%',
  marginTop: theme.spacing(4),
}));

const LogoutButton = styled(Button)({
  position: 'absolute',
  right: 100,
  top: 20,
});

const MoreMenuButton = styled(IconButton)({
  position: 'absolute',
  right: 20,
  top: 20,
});

const WalkerBoard = ({ onLogout }) => {
  const [error, setError] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [success, setSuccess] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);
  const [difficulty, setDifficulty] = useState('Unknown');
  const [tabIndex, setTabIndex] = useState(0);
  const [geojsonData, setGeojsonData] = useState(null);
  const mapRef = useRef(null);
  const navigate = useNavigate();

  const imageMap = {
    0: walkway0,
    1: walkway1,
    2: walkway2,
    3: walkway3,
  };

  const handleTabChange = (_, newValue) => {
    setTabIndex(newValue);
  };

  const handleLogOut = async () => {
    setError(null);
    setSuccess(null);

    try {
      await Axios.get("http://localhost:8080/logout");
      if (onLogout) {
        onLogout();
      }

      setSuccess('Log out successful!');
      navigate('/App');
    } catch (error) {
      setError('Log out failed: ' + error.message);
    }
  };

  useEffect(() => {
    setError(null);
    setSuccess(null);

    const checkFavoriteStatus = async () => {
      try {
        const response = await Axios.get("http://localhost:8080/favorites", {
          withCredentials: true,
        });

        if (response.data.favorites && response.data.favorites.some((favorite) => favorite.id === selectedMarker.id)) {
          setIsFavorite(true); 
        } else {
          setIsFavorite(false);
        }
      } catch (error) {
        console.error("Error checking favorite status:", error);
      }
    };

    if (selectedMarker) {
      checkFavoriteStatus();
    }
  }, [selectedMarker]);

  const handleFavoriteClick = async () => {
    setError(null);
    setSuccess(null);

    try {
      const response = await Axios.post(
        "http://localhost:8080/addFavorite",
        { locationId: selectedMarker.id },
        { withCredentials: true }
      );

      if (response.status === 200) {
        setSuccess("Marked as favorite!");
        setIsFavorite(true); 
        const points = 20;
        const responseP = await Axios.post("http://localhost:8080/addPoints", { points }, { withCredentials: true });
        if (responseP.status === 200) {
          setSuccess("Points added!");
        }
      }
    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError("This location is already marked as favorite!");
      } else {
        setError("Failed to mark as favorite: " + error.message);
      }
    }
  };

  const handleStartWalkClick = async () => {
    navigate('/StartWalk');
  };

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogoClick = () => {
    navigate('/WalkerBoard');
  };

  const handleCloseDialog = () => {
    setSelectedMarker(null);
  };

  const handleMarkerClick = useCallback(async (marker) => {
    setSelectedMarker(marker);
  }, []);

  useEffect(() => {
    if (selectedMarker) {
      const calculateDifficulty = (difficultyLevel) => {
        if (difficultyLevel === 1) {
          return 'Easy';
        } else if (difficultyLevel === 2) {
          return 'Medium';
        } else if (difficultyLevel === 3) {
          return 'Hard';
        } else {
          return 'Unknown';
        }
      };
      setDifficulty(calculateDifficulty(selectedMarker.difficulty));
    }
  }, [selectedMarker]);

  useEffect(() => {
    const fetchMarkers = async () => {
      try {
        const response = await Axios.get("http://localhost:8080/markers");
        setMarkers(response.data);
      } catch (error) {
        setError('Failed to fetch markers: ' + error.message);
      }
    };
    fetchMarkers();
  }, []);

  const transformedMarkers = markers.filter(marker => marker.coordinates).map(marker => ({
    ...marker,
    position: {
      lat: marker.coordinates.latitude,
      lng: marker.coordinates.longitude,
    },
    name: marker.name || 'Unknown Name',
    description: marker.description || 'No description available',
    image: marker.primaryImage || 'default_image.jpg',
    distance: marker.specifics?.distance || 'Unknown Distance',
    difficulty: marker.specifics?.difficulty || 'Unknown Difficulty',
    region: marker.region || 'Unknown Region',
    district: marker.district || 'Unknown District',
    defaultCenter: {
      lat: marker.coordinates.latitude,
      lng: marker.coordinates.longitude,
    },
  }));

  useEffect(() => {
    if (selectedMarker) {
      Axios.get(`http://localhost:8080/getGeojson`, { params: { walkwayId: selectedMarker.id } })
        .then((response) => {
          const geojsonData = response.data.geojson;
          if (geojsonData) {
            setGeojsonData(geojsonData);
            console.log("GeoJSON data fetched:", geojsonData);
          }
        })
        .catch((error) => {
          console.error('Error fetching GeoJSON:', error);
        });
    }
  }, [selectedMarker]);

  const handleMapLoad = (mapInstance) => {
    mapRef.current = mapInstance;
    console.log("Map instance loaded:", mapInstance);
  };

  useEffect(() => {
    if (geojsonData && mapRef.current) {
      console.log("Adding GeoJSON data to the map...");

      try {
        const dataLayer = new window.google.maps.Data();
        dataLayer.setMap(mapRef.current);

        const parsedGeoJson = typeof geojsonData === 'string' ? JSON.parse(geojsonData) : geojsonData;
        dataLayer.addGeoJson(parsedGeoJson);

        dataLayer.setStyle({
          strokeColor: "blue",
          strokeWeight: 2,
        });

        console.log("GeoJSON data added to the map.");
      } catch (error) {
        console.error("Error adding GeoJSON to the map:", error);
      }
    }
  }, [geojsonData]);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppContainer>
        <Logo src={logo} alt="logo" onClick={handleLogoClick} />
        <MoreMenuButton aria-label="more" onClick={handleClick}>
          <MoreVertIcon />
        </MoreMenuButton>

        <Menu anchorEl={anchorEl} keepMounted open={Boolean(anchorEl)} onClose={handleClose}>
          <MenuItem onClick={() => { handleClose(); navigate('/Profile'); }}>Profile</MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/History'); }}>History</MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/Favorites'); }}>Favorites</MenuItem>
        </Menu>

        <MapContainer>
          <APIProvider apiKey={'AIzaSyC8Sj12_rv73anPhi2oCeqsVVjUkfFJ4-U'}>
            <Map
              defaultZoom={10}
              defaultCenter={{ lat: 41.5564, lng: -8.16415 }}
              onLoad={handleMapLoad}
            >
              {transformedMarkers.map((marker) => (
                <AdvancedMarker
                  key={marker.id}
                  position={marker.position}
                  clickable={true}
                  onClick={() => handleMarkerClick(marker)}
                >
                  <Pin background={theme.palette.primary.secondary} borderColor={theme.palette.primary.main} glyphColor={theme.palette.primary.contrastText} />
                </AdvancedMarker>
              ))}
            </Map>
          </APIProvider>
        </MapContainer>

        <Dialog open={!!selectedMarker} onClose={handleCloseDialog} fullWidth>
          <DialogTitle>
            {selectedMarker?.name}
            <IconButton aria-label="close" onClick={handleCloseDialog}><CloseIcon /></IconButton>
          </DialogTitle>

          <Tabs value={tabIndex} onChange={handleTabChange}>
            <Tab label="Overview" />
            <Tab label="Walkway" />
            <Tab label="Services" />
          </Tabs>

          <DialogContent dividers>
            {tabIndex === 0 && (
              <>
                <img src={imageMap[selectedMarker?.id]} alt={selectedMarker?.name} style={{ maxWidth: '100%', marginBottom: '20px' }} />
                <Typography gutterBottom color="primary"><strong>District:</strong> {selectedMarker?.district}</Typography>
                <Typography gutterBottom color="primary"><strong>Region:</strong> {selectedMarker?.region}</Typography>
                <Typography gutterBottom color="secondary"><strong>Difficulty:</strong> {difficulty}</Typography>
                <Typography gutterBottom><strong>Description:</strong> {selectedMarker?.description}</Typography>
              </>
            )}
            {tabIndex === 1 && (
              <MapContainer>
                <APIProvider apiKey={'AIzaSyC8Sj12_rv73anPhi2oCeqsVVjUkfFJ4-U'}>
                  <Map
                    zoom={12}
                    defaultCenter={selectedMarker?.defaultCenter}
                    onLoad={handleMapLoad}
                    style={{ height: '400px', width: '100%' }}
                  />
                </APIProvider>
              </MapContainer>
            )}
            {tabIndex === 2 && <Typography>List of services offered along the walkway can be added here.</Typography>}
          </DialogContent>

          <DialogActions>
            <div>
              {!isFavorite ? (
                <Button variant="contained" color="primary" startIcon={<FavoriteIcon />} onClick={handleFavoriteClick}>
                  Mark as Favorite
                </Button>
              ) : (
                <Button variant="contained" disabled>
                  This location is already marked as favorite!
                </Button>
              )}

              {error && <div style={{ color: "red" }}>{error}</div>}
              {success && <div style={{ color: "green" }}>{success}</div>}
            </div>
            <Button variant="contained" color="secondary" startIcon={<DirectionsWalkIcon />} onClick={handleStartWalkClick}>
              Start Walk
            </Button>
          </DialogActions>
        </Dialog>
        <LogoutButton variant="contained" color="secondary" onClick={handleLogOut}>Logout</LogoutButton>
      </AppContainer>
    </ThemeProvider>
  );
};

export default WalkerBoard;
