                  type="number"
                  onChange={handleFormChange}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  required
                  label="Start Latitude"
                  name="trajectory.start.latitude"
                  type="number"
                  onChange={handleFormChange}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  required
                  label="Start Longitude"
                  name="trajectory.start.longitude"
                  type="number"
                  onChange={handleFormChange}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  required
                  label="End Latitude"
