                  type="number"
                  onChange={handleFormChange}
                  fullWidth
                  margin="normal"
                />
                <TextField
                  required
                  label="End Longitude"
                  name="trajectory.end.longitude"
                  type="number"
                  onChange={handleFormChange}
                  fullWidth
                  margin="normal"
                />
                <Button type="submit" variant="contained" color="primary">
                  Submit
                </Button>
              </form>
            </DialogContent>
            <DialogActions>
              <Button onClick={() => setShowFormDialog(false)} color="secondary">
                Cancel
