import React, { useEffect, useRef, useState } from "react";
import Axios from "axios";
import { Menu, MenuItem, Dialog, DialogContent, DialogActions, IconButton, Button, Container, CssBaseline, Typography, Paper, ThemeProvider } from '@mui/material';
import { styled } from '@mui/system';
import CloseIcon from '@mui/icons-material/Close';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FavoriteIcon from '@mui/icons-material/Favorite';
import DirectionsWalkIcon from '@mui/icons-material/DirectionsWalk';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api'; // Removed deprecated Marker import
import { useNavigate } from 'react-router-dom';

import theme from './Theme/theme';
import logo from './Theme/images/baselogo.jpg';
import walkway0 from './Theme/images/walkway_0.jpg';
import walkway1 from './Theme/images/walkway_1.jpg';
import walkway2 from './Theme/images/walkway_2.jpg';
import walkway3 from './Theme/images/walkway_3.jpg';
/* global google */

// Styled components using MUI's new styled API
const AppContainer = styled(Container)(({ theme }) => ({
  ...theme.root,
  zIndex: 9999,
}));

const Logo = styled('img')(({ theme }) => ({
  position: 'absolute',
  top: theme.spacing(2),
  left: theme.spacing(50),
  width: '200px',
  height: 'auto',
  cursor: 'pointer',
}));

const MapContainer = styled(Paper)(({ theme }) => ({
  height: '600px',
  width: '100%',
  marginTop: theme.spacing(4),
}));

const LogoutButton = styled(Button)(({ theme }) => ({
  position: 'absolute',
  right: 100,
  top: 20,
}));

const MoreMenuButton = styled(IconButton)(({ theme }) => ({
  position: 'absolute',
  right: 20,
  top: 20,
}));

const DialogTitleContainer = styled('div')({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
});

const WalkerBoard = ({ onLogout }) => {
  const [error, setError] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [success, setSuccess] = useState(null);
  const [markers, setMarkers] = useState([]);
  const [selectedMarker, setSelectedMarker] = useState(null);

  const navigate = useNavigate();
  const imageMap = {
    0: walkway0,
    1: walkway1,
    2: walkway2,
    3: walkway3,
  };

  // Handle Logout
  const handleLogOut = async () => {
    setError(null);
    setSuccess(null);

    try {
      await Axios.get("http://localhost:8080/logout");
      if (onLogout) {
        onLogout();
      }

      setSuccess('Log out successful!');
      navigate('/App');
    } catch (error) {
      setError('Log out failed: ' + error.message);
    }
  };

  const mapStyles = {
    height: '100%',
    width: '100%',
  };

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleLogoClick = () => {
    navigate('/WalkerBoard');
  };

  const handleFavoriteClick = () => {
    // Add logic for marking the place as favorite
    console.log(`${selectedMarker.name} marked as favorite`);
  };

  const handleStartWalkClick = () => {
    // Add logic for starting the walk
    console.log(`Starting walk to ${selectedMarker.name}`);
  };


  const handleCloseDialog = () => {
    setSelectedMarker(null);
  };

  useEffect(() => {
    const fetchMarkers = async () => {
      try {
        const response = await Axios.get("http://localhost:8080/markers");
        setMarkers(response.data);
      } catch (error) {
        setError('Failed to fetch markers: ' + error.message);
      }
    };

    fetchMarkers();
  });


  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppContainer>
        <Logo src={logo} alt="logo" onClick={handleLogoClick} />

        <MoreMenuButton
          aria-label="more"
          aria-controls="long-menu"
          aria-haspopup="true"
          onClick={handleClick}
        >
        <MoreVertIcon />
        </MoreMenuButton>

        <Menu
          id="long-menu"
          anchorEl={anchorEl}
          keepMounted
          open={Boolean(anchorEl)}
          onClose={handleClose}
          slotProps={{
            paper: {
              style: {
                maxHeight: 48 * 4.5,
                width: '20ch',
              },
            },
          }}
        >
          <MenuItem onClick={() => { handleClose(); navigate('/Profile'); }}>
            Profile
          </MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/History'); }}>
            History
          </MenuItem>
          <MenuItem onClick={() => { handleClose(); navigate('/Settings'); }}>
            Settings
          </MenuItem>
        </Menu>

        <MapContainer>
          <LoadScript
            googleMapsApiKey="AIzaSyC8Sj12_rv73anPhi2oCeqsVVjUkfFJ4-U"
            async
          >
            <GoogleMap
              mapContainerStyle={mapStyles}
              zoom={10}
              center={{ lat: 41.5564, lng: -8.16415 }}
              mapTypeId="terrain" 
            >
              {markers.map((marker) => (
                <Marker
                  key={marker.id}
                  position={{ lat: marker.coordinates.latitude, lng: marker.coordinates.longitude }}
                  title={marker.name}
                  onClick={() => setSelectedMarker(marker)}
                />
              ))}
            </GoogleMap>
          </LoadScript>
        </MapContainer>

        <Dialog
          open={selectedMarker !== null}
          onClose={handleCloseDialog}
          aria-labelledby="marker-details-title"
          fullWidth
        >
          <DialogTitleContainer>
            <Typography variant="h6">{selectedMarker?.name}</Typography>
            <IconButton aria-label="close" onClick={handleCloseDialog}>
              <CloseIcon />
            </IconButton>
          </DialogTitleContainer>
          <DialogContent dividers>
            {selectedMarker?.primaryImage && (
              <img 
                src={imageMap[selectedMarker.id]} 
                alt={selectedMarker.name} 
                style={{ maxWidth: '100%', marginBottom: '20px' }} 
              />
            )}
            <Typography gutterBottom>
              District: {selectedMarker?.district}
            </Typography>
            <Typography gutterBottom>
              Region: {selectedMarker?.region}
            </Typography>
            <Typography gutterBottom>
              Description: {selectedMarker?.description}
            </Typography>
          </DialogContent>
          <DialogActions>
            <Button
              variant="contained"
              color="primary"
              startIcon={<FavoriteIcon />}
              onClick={handleFavoriteClick}
            >
              Mark as Favorite
            </Button>
            <Button
              variant="contained"
              color="secondary"
              startIcon={<DirectionsWalkIcon />}
              onClick={handleStartWalkClick}
            >
              Start Walk
            </Button>
          </DialogActions>
        </Dialog>
        <LogoutButton variant="contained" color="secondary" onClick={handleLogOut}>
          Logout
        </LogoutButton>
      </AppContainer>
    </ThemeProvider>
  );
};

export default WalkerBoard;
